import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import LoginPage from './LoginPage'; // Ensure this path is correct
import Dashboard from './Dashboard'; // Make sure you have the Dashboard component

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);  // State to track login status
  const navigate = useNavigate(); // Hook to navigate programmatically

  const handleLogin = () => {
    setIsLoggedIn(true);  // Set logged in state to true after successful login
    navigate('/dashboard');  // Redirect to dashboard
  };

  return (
    <div style={{ minHeight: "100vh", padding: "2rem", backgroundColor: "#f7fafc" }}>
      {!isLoggedIn ? (
        // Render the LoginPage if not logged in
        <LoginPage onLogin={handleLogin} />
      ) : (
        // Render the Dashboard page once logged in
        <div>
          <h1 style={{ fontSize: "2.5rem", fontWeight: "bold", textAlign: "center", marginBottom: "2rem" }}>
            Inventory Management Hub
          </h1>
          {/* The Dashboard page content */}
          <Dashboard />
        </div>
      )}
    </div>
  );
}

function AppWrapper() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<App />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
}

export default AppWrapper;
